import React, { useState, useEffect, useMemo } from 'react';
import { getAppointments, updateAppointmentStatus } from "./appointment_service";
// Import the new components
import DashboardCards from "./DashboardCards";
import StatusTabs from "./StatusTabs";
import AppointmentCard from "./AppointmentCard";
import StatusLegend from "./StatusLegend"; // Assuming Calendar.js is also imported/used elsewhere

// Utility function to get today's date in YYYY-MM-DD format
const getTodayDate = () => new Date().toISOString().split('T')[0];

export default function AppointmentManagementView() {
  const [appointments, setAppointments] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null); // State for Calendar Filtering
  const [activeTab, setActiveTab] = useState('All'); // State for Tab Filtering
  const [showForm, setShowForm] = useState(false); // For AppointmentForm

  // --- 1. DATA FETCHING (Task 2.1 & 2.2) ---
  // Fetches data on initial load OR when selectedDate changes (Calendar filtering)
  useEffect(() => {
    // Note: The assignment specifies filtering based on date only for the calendar click.
    // We pass the filter object to the Python function only when a date is clicked.
    const filters = selectedDate ? { date: selectedDate } : {};

    getAppointments(filters).then(data => {
      setAppointments(data);
    });
  }, [selectedDate]); // Re-run effect when selectedDate changes

  // --- 2. STATUS UPDATE (Task 2.4) ---
  const handleUpdateStatus = async (id, newStatus) => {
    try {
      const updated = await updateAppointmentStatus(id, newStatus);

      // Refresh the local component state to reflect the change (simulating real-time updates) 
      setAppointments(prev =>
        prev.map(a => (a.id === updated.id ? updated : a))
      );
    } catch (error) {
      console.error("Error updating appointment status:", error);
    }
  };

  // --- 3. FILTERING LOGIC (Task 2.3) ---
  // Memoize the filtered list to avoid re-calculating on every render
  const filteredAppointments = useMemo(() => {
    const today = getTodayDate();

    // Start with appointments filtered by Calendar (selectedDate filter is applied in useEffect/getAppointments)
    let list = appointments;

    // Apply Tab Filtering
    if (activeTab === 'Today') {
      return list.filter(appt => appt.date === today);
    } else if (activeTab === 'Upcoming') {
      return list.filter(appt => appt.date > today);
    } else if (activeTab === 'Past') {
      return list.filter(appt => appt.date < today);
    }
    // If 'All' is selected, return the list as is (filtered by selectedDate if any)
    return list;
  }, [appointments, activeTab]);

  // --- Dashboard Card Counts ---
  const counts = useMemo(() => {
    const today = getTodayDate();
    return {
      todayCount: appointments.filter(app => app.date === today).length,
      confirmedCount: appointments.filter(app => app.status === 'Confirmed').length,
      upcomingCount: appointments.filter(app => app.date > today).length,
      virtualCount: appointments.filter(app => app.mode === 'Virtual').length, // Assuming mode is 'Virtual' for telemedicine
    };
  }, [appointments]);

  // --- 4. CALENDAR CLICK HANDLER (Task 2.2) ---
  const handleDateClick = (date) => {
      // Set the local state for the selectedDate 
      setSelectedDate(date); 
      // The useEffect hook above will call get_appointments() passing the selectedDate as a filter [cite: 25]
      // This will update the list of appointments displayed [cite: 26]
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      {/* Changed max-w-4xl to max-w-7xl for the dashboard layout */}
      <div className="max-w-7xl mx-auto"> 

        {/* 1. HEADER: Title and Buttons */}
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-gray-900">Appointment Management</h1>
          <div className="flex space-x-3">
            <button className="px-4 py-2 text-gray-600 border border-gray-300 rounded-lg hover:bg-gray-50">
              Export
            </button>
            <button
              onClick={() => setShowForm(true)}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 font-semibold"
            >
              + New Appointment
            </button>
          </div>
        </div>

        {/* 2. DASHBOARD CARDS */}
        <DashboardCards {...counts} />


        {/* 3. MAIN TWO-COLUMN CONTENT AREA */}
        {/* Added background and shadow to match the main content panel in the mockup */}
        <div className="flex mt-6 bg-white p-6 rounded-xl shadow-lg border border-gray-100">

          {/* LEFT COLUMN: Calendar & Status Legends (w-1/4) */}
          <div className="w-1/4 pr-6 border-r border-gray-200">
            <h2 className="text-xl font-semibold mb-4 text-gray-800">Calendar</h2>

            {/* Note: Your Calendar.js component needs to be updated to render the full month view */}
            {/* You would replace the existing Calendar filter list with the full component */}
            {/* For now, we simulate the Calendar component with a simple placeholder: */}
            {/* <Calendar onDateClick={handleDateClick} selectedDate={selectedDate} /> */}
            <div className="h-64 bg-gray-50 p-4 flex items-center justify-center rounded-lg border">
                <p className="text-sm text-gray-500">
                    Full Calendar Widget goes here. <br />Clicking a date calls `handleDateClick`
                </p>
            </div>


            {/* Status Legends */}
            <StatusLegend />
          </div>

          {/* RIGHT COLUMN: Filters, Tabs, and Appointment List (w-3/4) */}
          <div className="w-3/4 pl-6">

            {/* Status Tabs (Task 2.3) */}
            <StatusTabs activeTab={activeTab} onTabChange={setActiveTab} />

            {/* Search and Filter Bar (Placeholder matching mockup) */}
            <div className="flex space-x-3 mb-6">
                <input 
                    type="text" 
                    placeholder="Search appointments..." 
                    className="flex-grow p-2 border border-gray-300 rounded-lg"
                />
                <select className="p-2 border border-gray-300 rounded-lg w-32">
                    <option>All Status</option>
                    {/* Options: Confirmed, Scheduled, Cancelled */}
                </select>
                <select className="p-2 border border-gray-300 rounded-lg w-32">
                    <option>All Doctors</option>
                    {/* Options: Dr. Rajati, Dr. Shwma, etc. */}
                </select>
            </div>

            {/* Appointment List (Using the new AppointmentCard) */}
            <div className="space-y-4">
              {filteredAppointments.length > 0 ? (
                filteredAppointments.map(appt => (
                  <AppointmentCard 
                    key={appt.id} 
                    appointment={appt} 
                    onUpdateStatus={handleUpdateStatus} // Pass the status update function
                  />
                ))
              ) : (
                <p className="text-center text-gray-500 mt-10">
                    No appointments found for the selected filter ({activeTab}{selectedDate ? ` on ${selectedDate}` : ''}).
                </p>
              )}
            </div>
          </div>
        </div>

        {/* Removed the old DailyTimeline and Appointment list rendering */}

        {/* Add Appointment Modal */}
        {showForm && (
          <AppointmentForm
            onSave={(newAppt) => {
              // Assuming AppointmentForm will include all required fields (name, date, time, etc.)
              setAppointments(prev => [...prev, { id: Date.now(), ...newAppt, status: 'Scheduled' }]);
              setShowForm(false);
            }}
            onCancel={() => setShowForm(false)}
          />
        )}
      </div>
    </div>
  );
}
