// Conceptual update for AppointmentForm.jsx

import React, { useState } from 'react';

// Assuming you have initial state fields like this:
const initialFormState = {
  name: '',         // Patient Name
  doctorName: '',   // Doctor Name
  date: '',         
  time: '',
  status: 'Scheduled',
  // ðŸ’¥ ADD THESE MISSING FIELDS
  reason: '',        
  consultation: '',
};

export default function AppointmentForm({ onSave, onCancel }) {
  const [formData, setFormData] = useState(initialFormState);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validate form data here (optional)

    // Call the onSave function with the complete data, including new fields
    onSave(formData);
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-xl w-96">
      <h2 className="text-xl font-bold mb-4">Add Appointment</h2>
      <form onSubmit={handleSubmit}>
        {/* Existing Input: Patient Name */}
        <div className="mb-4">
          <label className="block text-sm font-medium mb-1">Patient Name</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required className="w-full p-2 border rounded" />
        </div>

        {/* ... (Other existing fields: Doctor Name, Date, Time, Status) ... */}

        {/* ðŸ’¥ NEW INPUT FIELD: REASON */}
        <div className="mb-4">
          <label className="block text-sm font-medium mb-1">Reason for Visit</label>
          <input type="text" name="reason" value={formData.reason} onChange={handleChange} required className="w-full p-2 border rounded" />
        </div>

        {/* ðŸ’¥ NEW INPUT FIELD: CONSULTATION */}
        <div className="mb-6">
          <label className="block text-sm font-medium mb-1">Consultation Type</label>
          <input type="text" name="consultation" value={formData.consultation} onChange={handleChange} required className="w-full p-2 border rounded" />
        </div>

        {/* Action Buttons */}
        <div className="flex justify-end space-x-3">
          <button type="button" onClick={onCancel} className="px-4 py-2 text-gray-700 border rounded">
            Cancel
          </button>
          <button type="submit" className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
            Save
          </button>
        </div>
      </form>
    </div>
  );
}
