import React, { useState, useEffect, useMemo } from 'react';
import { getAppointments, updateAppointmentStatus } from "./appointment_service";

// Import Custom Components (Ensure these files are in the same directory: src/)
import DashboardCards from "./DashboardCards";
import StatusTabs from "./StatusTabs";
import AppointmentCard from "./AppointmentCard";
import StatusLegend from "./StatusLegend"; 

// NOTE: Check the path for AppointmentForm.
// If it is inside a 'components' folder, change the path below to: 
// import AppointmentForm from "./components/AppointmentForm"; 
import AppointmentForm from "./AppointmentForm"; 


// Utility function to get today's date in YYYY-MM-DD format for filtering
const getTodayDate = () => new Date().toISOString().split('T')[0];

export default function AppointmentManagementView() {
  const [appointments, setAppointments] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null); // State for Calendar Filtering (Task 2.2)
  const [activeTab, setActiveTab] = useState('All');     // State for Tab Filtering (Task 2.3)
  const [showForm, setShowForm] = useState(false);       // For 'Add Appointment' Modal

  // --- 1. DATA FETCHING (Task 2.1 & 2.2) ---
  // Fetches data on initial load OR when selectedDate changes (Calendar filtering).
  useEffect(() => {
    // Only pass date filter if a date is selected via the calendar
    const filters = selectedDate ? { date: selectedDate } : {};

    getAppointments(filters).then(data => {
      setAppointments(data);
    });

    // Reset tab filter when calendar date changes, to prevent conflict
    setActiveTab('All');

  }, [selectedDate]); // Re-run effect when selectedDate changes

  // --- 2. STATUS UPDATE (Task 2.4) ---
  const handleUpdateStatus = async (id, newStatus) => {
    try {
      // Call the backend mutation function
      const updated = await updateAppointmentStatus(id, newStatus);

      // ðŸ’¥ FIX APPLIED HERE: Robust check for valid data before mapping
      if (updated && updated.id !== undefined && updated.id !== null) {
          // Refresh the local component state with the updated appointment
          setAppointments(prev =>
            prev.map(a => (a.id === updated.id ? updated : a))
          );
      } else {
          // Log specific error if API returned an invalid object (the root cause of your crash)
          console.error(`Status update failed for ID ${id}. API returned an invalid object or undefined. Please check 'appointment_service.py' for unhandled ValueError exception.`, updated);
      }

    } catch (error) {
      // This catches connection errors or exceptions raised by the Python service
      console.error("Critical error during status update:", error);
    }
  };

  // --- 3. TAB FILTERING LOGIC (Task 2.3) ---
  // Calculates the list of appointments to display based on the selected tab
  const filteredAppointments = useMemo(() => {
    const today = getTodayDate();
    let list = appointments;

    // Apply Tab Filtering logic
    if (activeTab === 'Today') {
      return list.filter(appt => appt.date === today);
    } else if (activeTab === 'Upcoming') {
      // Compare dates as strings (works for YYYY-MM-DD format)
      return list.filter(appt => appt.date > today); 
    } else if (activeTab === 'Past') {
      return list.filter(appt => appt.date < today);
    }
    // 'All' tab returns the full list (which is already filtered by selectedDate if set)
    return list; 
  }, [appointments, activeTab]);

  // --- Dashboard Card Counts Calculation ---
  const counts = useMemo(() => {
    const today = getTodayDate();
    return {
      todayCount: appointments.filter(app => app.date === today).length,
      confirmedCount: appointments.filter(app => app.status === 'Confirmed').length,
      upcomingCount: appointments.filter(app => app.date > today).length,
      virtualCount: appointments.filter(app => app.mode === 'Virtual').length, 
    };
  }, [appointments]);

  // --- 4. CALENDAR CLICK HANDLER (Task 2.2) ---
  const handleDateClick = (date) => {
      // date should be passed in YYYY-MM-DD format
      setSelectedDate(date); 
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      {/* Container widened to max-w-7xl for the dashboard style */}
      <div className="max-w-7xl mx-auto"> 

        {/* HEADER: Title and Buttons */}
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-gray-900">Appointment Management</h1>
          <div className="flex space-x-3">
            <button className="px-4 py-2 text-gray-600 border border-gray-300 rounded-lg hover:bg-gray-50">
              Export
            </button>
            <button
              onClick={() => setShowForm(true)}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 font-semibold"
            >
              + New Appointment
            </button>
          </div>
        </div>

        {/* DASHBOARD CARDS */}
        <DashboardCards {...counts} />


        {/* MAIN TWO-COLUMN CONTENT AREA (Matches Mockup Structure) */}
        <div className="flex mt-6 bg-white p-6 rounded-xl shadow-lg border border-gray-100">

          {/* LEFT COLUMN: Calendar & Status Legends (w-1/4) */}
          <div className="w-1/4 pr-6 border-r border-gray-200">
            <h2 className="text-xl font-semibold mb-4 text-gray-800">Calendar</h2>

            {/* Placeholder for Calendar Component */}
            {/* Replace this div with your implemented <Calendar onDateClick={handleDateClick} selectedDate={selectedDate} /> */}
            <div className="h-64 bg-gray-50 p-4 flex items-center justify-center rounded-lg border">
                <p className="text-sm text-gray-500 text-center">
                    **Calendar Component**<br />
                    Clicking a date calls `handleDateClick`
                </p>
            </div>
            {selectedDate && (
                 <p className="text-sm text-gray-500 mt-2 text-center">
                    Active Filter: <b>{selectedDate}</b>
                </p>
            )}

            {/* Status Legends */}
            <StatusLegend />
          </div>

          {/* RIGHT COLUMN: Filters, Tabs, and Appointment List (w-3/4) */}
          <div className="w-3/4 pl-6">

            {/* Status Tabs (Task 2.3 Integration) */}
            <StatusTabs activeTab={activeTab} onTabChange={setActiveTab} />

            {/* Search and Filter Bar (Mockup elements) */}
            <div className="flex space-x-3 mb-6">
                <input 
                    type="text" 
                    placeholder="Search appointments..." 
                    className="flex-grow p-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
                />
                <select className="p-2 border border-gray-300 rounded-lg w-32 focus:ring-blue-500 focus:border-blue-500">
                    <option>All Status</option>
                    <option>Confirmed</option>
                    <option>Scheduled</option>
                    <option>Cancelled</option>
                    <option>Completed</option>
                </select>
                <select className="p-2 border border-gray-300 rounded-lg w-32 focus:ring-blue-500 focus:border-blue-500">
                    <option>All Doctors</option>
                    <option>Dr. Rajeev Kumar</option>
                    <option>Dr. Priya Sharma</option>
                    <option>Dr. Neha Patel</option>
                </select>
            </div>

            {/* Appointment List (Renders the filtered list using AppointmentCard) */}
            <div className="space-y-4">
              {filteredAppointments.length > 0 ? (
                filteredAppointments.map(appt => (
                  <AppointmentCard 
                    key={appt.id} 
                    appointment={appt} 
                    onUpdateStatus={handleUpdateStatus} // Mutation handler
                  />
                ))
              ) : (
                <p className="text-center text-gray-500 mt-10 p-4 border rounded-lg bg-gray-50">
                    No appointments found for the current selection.
                </p>
              )}
            </div>
          </div>
        </div>

        {/* Add Appointment Modal */}
        {showForm && (
          <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50">
            <AppointmentForm
              onSave={(newAppt) => {
                // When a new appointment is created, add it to the list
                setAppointments(prev => [...prev, { id: Date.now(), ...newAppt, status: 'Scheduled' }]);
                setShowForm(false);
              }}
              onCancel={() => setShowForm(false)}
            />
          </div>
        )}
      </div>
    </div>
  );
}
