import React,{ userState, useEffect, useCallback} from 'react';
import {getAppointment, updateAppointmentStatus} from "./appointment_service";

const isDateToday = (dateString) => {
  const today = new Date();
  const targetDate = new Date(dateString);
  return today.toDateString() === targetDate.toDateString();
};
const isDateUpcoming = (dateString) =>{
  const today = new Date();
  const targetDate = new Date(dateString);
  today.setHours(0,0,0,0);
  targetDate.setHours(0,0,0,0);
  return targetDate > today;
};
const isDatePast = (dateString) =>{
  const today = new Date();
  const targetDate = new Date (dateString);
  today.setHours(0,0,0,0);
  targetDate.setHours(0,0,0,0);
  return targetDate  < today;
};
function AppoitmentManagementView(){
  const [appointments, setAppointments] = userState([]); //state list of appoin to display
  const [selectedDate, setSelectedDate] = userState(null); //state date selcted from calender
  const [selectedTab , setSelectedTab] = userState('All'); //state for curentl tab - today, past , all
  const fetchAppointments = useCallback(async() =>{ 
    const dateFilter = selectedDate ? selectedDate : undefined;
    let fetchedData = await getAppointments({date: dateFilter});
    if(selectedTab !== 'All') {
      filteredByTab = fetchedData.filter(appt => {
        switch(selectedTab){
          case 'Today':
            return isDateToday(appt.date);
          case 'Upcoming':
            return isDateUpcoming(appt.date);
          case 'Past':
            return isDatePast(appt.date);
          default:
            return true;
        }
      });

    }
    setAppointments(filteredByTab);

  },[selectedDate, selectedTab]);
  useEffect(() => {
    fetchAndFilterAppointments();
  },[fetchAndFilterAppointments]);
  const handleDateChange = (date) =>{
    setSelectedDate(date);
    setSelectedTab(null);
  };
  const handleTabClick = (tab) =>{
    setSelectedTab(tab);
    setSelectedDate(null);
  };
  const handleStatusUpdate =  async (id , newStatus) => {
    const updateAppt = await updateAppointmentStatus(id , newStatus);
    if (updateApp) {
      fetchAndFilterAppointments();
    }
  };
  return (
    <div className = "appointment-management-view p-4">
      <h1>Appointment Management</h1>
      <p> Schedule and manage patient appointments</p>
      {/* calender widger filer*/}
      <div className ="calender-widget mb-4 p-4 border rounded">
        <h3> Calender</h3>
        <button className="p-2 border rounded m-1" onClick={() => handleDateClick('2025-01-12')} >
          Filter by january 12</button> 
        <button className="p-2 border rounded m-1" onClick={() => handleDateClick('2025-01-13')} >
          Filter by january 13</button> 
        <button className ="p-2 border rounded m-1" onclick = {()=> handleDateClick(null)}>
          clear date filter
        </button>
        <p className="mt-2"> Current date filter: **{selectedDate || 'None'} ** </p>
      </div>
      {/* tabbed view for today, upcoming, past , all */}
      <div className="tab-filters mb-4">
        {['All', 'Today', 'Upcoming', 'Past'].map(tab => (
          <button
              key={tab}
              className={`p-2 m-1 rounded ${selectedTab === tab ? 'bg-blue-500 text-white' : 'border'}`}
              onClick={()=> handleTabClick(tab)}
          >
            {tab} 
          </button>
        ))}
      </div>
      {/* appointment list */}
      <div className="appointment-list">
        <h2>Appoinments for {selectedTab || selectedDate}</h2>
        {appointments.length> 0 ? (
          appointments.map(appt => (
            <div key = {appt.id} className="appointment-card p-3 my-2 border rounded shadow-sm">
              <h3 className="font-bold">{appt.name}-{appt.time}</h3>
              <p>Date: {appt.date} | Doctor: {appt.doctorName}</p>
              <p>Mode: {appt.mode} | Duration: {appt.duration || 'N/A'}</p>
              <p>Status: <span className="font-bold text-green-600">{appt.status}</span></p>

              {/* buttons to update status */}
              {appt.status !== 'cancelled'&& (
                <button 
                className="mt-2 px-3 py-1 bg-red-500 text-white rounded hover:bg-red-700"
                onClick={() => handleStatusUpdate(appt.id, 'cancelled')}
                >
                    Cancel Appoinment
                </button>


              )}
            </div> 
          )) 
        ):(
          <p className="p-4 bg-yellow-100 rounded">No appointments matching current filters </p>
        )}
      </div>
    </div>
  );
}
export default AppoitmentManagementView;
